const PIXI = window.PIXI
const { get } = require('axios')
const { FullscreenApplication } = require('./app')
const { TextureExtractor } = require('./extract')
const { tiled } = require('./tiled')
const { utils } = require('./utils')

class TiledApplication extends FullscreenApplication {
    constructor(everyTick, options) {
        super(everyTick, options)
    }

    createTiles(config) {
        this.tiles = new TextureExtractor(config)
    }

    async createWorld(world, tileset, tilesize, group = [], clear = [], pickable = []) {
        if (!this.tiles) console.warn('Creating tile-less world. You should propably first call createTiles()')
        const { data } = await get(world)
        this.world = tiled.makeTiledWorld(data, tileset)
        this.ground = this.world.children.filter(({ type }) => type === 'tilelayer')
        this.objects = this.createObjects(tilesize, group, clear, pickable)
        this.sprites = this.createSprites(tilesize, clear)
        this.ground.forEach((ground) => this.stage.addChild(ground))
        this.sprites.forEach((sprite) => this.stage.addChild(sprite))
    }

    createObjects(tilesize, group = [], clear = [], pickable = []) {
        const objects = []
        const groups = utils.groupObjects(this.world.objects)
        const groupRegExp = group.map((string) => new RegExp(string))
        Object.keys(groups).filter((name) => {
            const markedForRemove = utils.contains(clear, name)
            return (!markedForRemove && !utils.contains(pickable, name))
        }).forEach((name) => {
            console.log(`┣━ parse object: ${name}`)
            const next = groups[name]
            const group = groupRegExp.find((regExp) => name.match(regExp))
            let container
            if (group) {
                container = new PIXI.Container()
                utils.pushObject(container, objects)
                utils.nameObject(container, { name, type: 'layer' })
                console.log(`┣━ add layer: ${name}`)
            }
            if (Array.isArray(next)) {
                next.forEach((object) => {
                    if (object.gid) {
                        const sprite = utils.createSprite(object.gid, tilesize, this.tiles)
                        if (sprite) {
                            utils.nameObject(sprite, object, ['x', 'y', 'gid', 'name'])
                            if (group) {
                                console.log(`┣━ add object ${object.name} (${~~sprite.x}/${~~sprite.y}) to layer: ${name}`)
                                container.addChild(sprite)
                            } else {
                                utils.pushObject(sprite, objects)
                            }
                        }
                    }
                })
            }
        })
        return objects
    }

    createSprites(tilesize = 32, clear = []) {
        return this.objects
            .filter((sprite) => !utils.contains(clear, sprite.name))
            .map((sprite) => {
                try {
                    const x = Math.round((sprite.x || 0) + (sprite.offsetx || 0))
                    const y = Math.round((sprite.y || 0) + (sprite.offsety || 0))
                    const objects = sprite.children.length ? sprite.children : [sprite]
                    const bounds = utils.getBounds(objects)
                    sprite.x = sprite.basex = bounds.left + x
                    sprite.y = sprite.basey = bounds.down + y - tilesize
                    objects.forEach((child) => {
                        child.x -= bounds.left - child.width * 0.5
                        child.y -= bounds.down - tilesize
                        child.anchor.set(0.5, 1)
                    })
                    return sprite
                } catch (err) {
                    console.error(err.message, err.stack)
                }
            })
    }
}

export { TiledApplication }

const PIXI = window.PIXI

const utils = {

    getTexture(frame, tiles) {
        if (tiles && (typeof tiles.getFrame === 'function')) {
            return tiles.getFrame(frame)
        } else {
            console.warn('! tiles not set @ getTexture')
        }
        return PIXI.Texture.EMPTY
    },

    createSprite(frame, tilesize = 32, tiles = null) {
        let sprite
        if (tiles) {
            if (!isNaN(frame) && isFinite(frame)) {
                sprite = new PIXI.Sprite(utils.getTexture(~~frame, tiles))
            } else if (frame) {
                sprite = PIXI.Sprite.fromFrame(frame)
            }
        } else {
            sprite = new PIXI.Sprite()
        }
        if (sprite) {
            sprite.width = tilesize
            sprite.height = tilesize
            if (sprite.texture) {
                sprite.texture.baseTexture.scaleMode = PIXI.SCALE_MODES.NEAREST
            }
            return sprite
        }
    },

    getModel(source, url) {
        let cursor = source
        const propList = url.split('.')
        while (cursor && propList.length) {
            cursor = cursor[propList.shift()]
        }
        return cursor
    },

    contains(array, value) {
        return value && array.some((string) => value.startsWith(string))
    },

    groupObjects(array, groupName = 'group.name') {
        return array ? array.reduce((groups, item) => {
            const cursor = utils.getModel(item, groupName)
            if (cursor) {
                if (!groups[cursor]) {
                    groups[cursor] = []
                }
                groups[cursor].push(item)
            }
            return groups
        }, {}) : {}
    },

    nameObject(target, source = {}, props = ['name', 'type']) {
        if (!target) return
        props.forEach((prop) => {
            if (source[prop] && !target[prop]) {
                target[prop] = source[prop]
            }
        })
    },

    pushObject(source, target, props) {
        if (target.indexOf(source) !== -1) return
        if (Array.isArray(source.objects)) {
            source.objects.forEach((sprite) => {
                utils.nameObject(sprite, source, props)
            })
        }
        target.push(source)
    },

    getBounds(array) {
        if (Array.isArray(array) && array.length) {
            let up = Infinity
            let left = Infinity
            let right = -Infinity
            let down = -Infinity
            let temp
            array.forEach((sprite) => {
                const w = sprite.width
                const h = sprite.height
                if ((temp = sprite.x) < left) {
                    left = temp
                }
                if ((temp = sprite.y) < up) {
                    up = temp
                }
                if ((temp = sprite.x + w) > right) {
                    right = temp
                }
                if ((temp = sprite.y + h) > down) {
                    down = temp
                }
            })
            return { up, down, left, right }
        }
        return { up: 0, down: 0, left: 0, right: 0 }
    }
}

export { utils }
